mapper.delete:
DELETE FROM mmall_cart
WHERE user_id = #{userId}

  AND product_id IN
  
    #{item}

mapper.update:
UPDATE mmall_cart
SET checked = #{checked},
update_time = now()
WHERE user_id = #{userId}

  AND product_id = #{productId}

mapper.select:

select id, user_id, product_id, quantity, checked, create_time, update_time
from mmall_cart
where id = #{id,jdbcType=INTEGER}

SELECT id, user_id, product_id, quantity, checked, create_time, update_time
FROM mmall_cart
WHERE user_id = #{userId}
AND product_id = #{productId}

SELECT id, user_id, product_id, quantity, checked, create_time, update_time
FROM mmall_cart
WHERE user_id = #{userId}
